INSERT INTO `wp_options` VALUES (159,'_transient_newsup_about_plugin_info_woocommerce','O:8:\"stdClass\":22:{s:4:\"name\";s:11:\"WooCommerce\";s:4:\"slug\";s:11:\"woocommerce\";s:7:\"version\";s:5:\"4.0.1\";s:6:\"author\";s:48:\"<a href=\"https://woocommerce.com\">Automattic</a>\";s:14:\"author_profile\";s:40:\"https://profiles.wordpress.org/woothemes\";s:12:\"contributors\";a:10:{s:10:\"automattic\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/automattic\";s:6:\"avatar\";s:82:\"http://0.gravatar.com/avatar/687b3bf96c41800814e3b93766444283?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:10:\"Automattic\";}s:10:\"mikejolley\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/mikejolley\";s:6:\"avatar\";s:82:\"http://0.gravatar.com/avatar/9b87f99f47e30735e1136c5141531fdf?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:11:\"Mike Jolley\";}s:11:\"jameskoster\";a:3:{s:7:\"profile\";s:42:\"https://profiles.wordpress.org/jameskoster\";s:6:\"avatar\";s:82:\"http://1.gravatar.com/avatar/19a6a101c57df54bc1c40adcfba785d8?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:12:\"James Koster\";}s:14:\"claudiosanches\";a:3:{s:7:\"profile\";s:45:\"https://profiles.wordpress.org/claudiosanches\";s:6:\"avatar\";s:82:\"http://0.gravatar.com/avatar/908480753c07509e76322dc17d305c8b?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:15:\"Claudio Sanches\";}s:5:\"kloon\";a:3:{s:7:\"profile\";s:36:\"https://profiles.wordpress.org/kloon\";s:6:\"avatar\";s:82:\"http://2.gravatar.com/avatar/e5aff129655270463bb81a469440e5fc?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:17:\"Gerhard Potgieter\";}s:13:\"rodrigosprimo\";a:3:{s:7:\"profile\";s:44:\"https://profiles.wordpress.org/rodrigosprimo\";s:6:\"avatar\";s:82:\"http://0.gravatar.com/avatar/f2271ae6814a29d8cbf38ae2d0305a72?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:13:\"Rodrigo Primo\";}s:15:\"peterfabian1000\";a:3:{s:7:\"profile\";s:46:\"https://profiles.wordpress.org/peterfabian1000\";s:6:\"avatar\";s:82:\"http://2.gravatar.com/avatar/be249b86dddbf495565a36196625d77a?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:15:\"peterfabian1000\";}s:7:\"vedjain\";a:3:{s:7:\"profile\";s:38:\"https://profiles.wordpress.org/vedjain\";s:6:\"avatar\";s:82:\"http://1.gravatar.com/avatar/7a640eb2847d9a0a0d5e6af385e21881?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:13:\"Vedanshu Jain\";}s:8:\"jamosova\";a:3:{s:7:\"profile\";s:39:\"https://profiles.wordpress.org/jamosova\";s:6:\"avatar\";s:82:\"http://2.gravatar.com/avatar/eb515897a71b0df82ccd2788548588c4?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:13:\"Julia Amosova\";}s:16:\"obliviousharmony\";a:3:{s:7:\"profile\";s:47:\"https://profiles.wordpress.org/obliviousharmony\";s:6:\"avatar\";s:82:\"http://1.gravatar.com/avatar/185f31c1e5af65fee78506769ca13276?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:16:\"obliviousharmony\";}}s:12:\"requires_php\";s:3:\"7.0\";s:7:\"ratings\";a:5:{i:5;i:2979;i:4;i:142;i:3;i:73;i:2;i:73;i:1;i:256;}s:11:\"num_ratings\";i:3523;s:15:\"support_threads\";i:1826;s:24:\"support_threads_resolved\";i:1244;s:15:\"active_installs\";i:5000000;s:8:\"homepage\";s:24:\"https://woocommerce.com/\";s:8:\"sections\";a:6:{s:11:\"description\";s:16373:\"<p>WooCommerce is a flexible, open-source eCommerce solution built on WordPress. Whether you&#8217;re launching a business, taking an existing brick and mortar store online, or designing sites for clients you can get started quickly and build exactly the store you want.</p>\n<p>Activate the free WooCommerce plugin on a new or existing WordPress site, follow the optional guided tour, and set up a new store in minutes with:</p>\n<ul>\n<li>Product, Cart, and Checkout pages</li>\n<li>Secure payments by credit card and alternatives</li>\n<li>Configurable shipping options, including flat rates and <a href=\"https://woocommerce.com/products/shipping/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">label printing</a></li>\n<li>Integrate content and commerce across your site via modular blocks</li>\n<li><a href=\"https://woocommerce.com/products/tax/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Automated tax calculations</a></li>\n<li><a href=\"https://woocommerce.com/products/woocommerce-google-analytics/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Google Analytics</a>, <a href=\"https://woocommerce.com/products/mailchimp-for-woocommerce/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">MailChimp</a>, and <a href=\"https://woocommerce.com/products/facebook/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Facebook</a> integration</li>\n<li><a href=\"https://woocommerce.com/posts/woocommerce-admin-a-new-central-dashboard-for-woocommerce/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Central store dashboard</a> with key metrics, and more.</li>\n</ul>\n<p>Beyond the basics, WooCommerce is fully customizable and extendable:</p>\n<ul>\n<li>Choose a design to suit your brand and industry.</li>\n<li>Enhance your store with free and paid extensions: add features and integrate with local and global eCommerce web services.</li>\n<li>Inspect and modify any aspect of the core plugin code.</li>\n<li>Leverage hooks and filters to modify functions.</li>\n<li>Build on top of the REST API and webhooks.</li>\n</ul>\n<p>Open-source freedoms mean full ownership of content and data forever  plus the expertise of a friendly <a href=\"https://woocommerce.com/meetups/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">global community</a>.</p>\n<p>WooCommerce is built to allow store managers to run an eCommerce site themselves, no web developer needed. There is also a free WooCommerce <a href=\"https://woocommerce.com/mobile/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">mobile app</a> (Android and iOS) for store management on the go.</p>\n<p>WooCommerce is developed and supported by Automattic, the creators of Jetpack and WordPress.com, along with independent contributors. The <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">official extension marketplace</a> is on WooCommerce.com.</p>\n<h4>From subscriptions to gym classes to luxury cars</h4>\n<p>With WooCommerce, you can sell both physical and digital goods in all shapes and sizes, offer product variations, complex configurations, and instant downloads to shoppers; and even sell affiliate goods from online marketplaces.</p>\n<p>And those are just the out-of-the-box options. With paid extensions, you can extend your WooCommerce store to take bookings, offer memberships, set up recurring payments by subscription, create dynamic pricing rules, and much more.</p>\n<p>Start a monthly wine subscription box, offer a discount on yoga mats to members whove attended 10+ classes, offer configurable hampers or personalized jewelry  its all possible with WooCommerce.</p>\n<h4>Comprehensive payment options</h4>\n<p>WooCommerce comes bundled with the ability to accept major credit cards, alternative payment methods, BACS (bank transfers), and cash on delivery.</p>\n<p>For additional options, WooCommerce also integrates with more than 140 region-specific gateways including popular choices like <a href=\"https://woocommerce.com/products/stripe/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Stripe</a>, <a href=\"https://woocommerce.com/products/woocommerce-gateway-paypal-checkout/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">PayPal</a>, <a href=\"https://woocommerce.com/products/square/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Square</a> and <a href=\"https://woocommerce.com/products/pay-with-amazon/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Amazon Pay</a>. <a href=\"https://woocommerce.com/apple-pay/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Apple Pay</a> and Google Pay are also supported.</p>\n<p>Search for your payment service provider of choice on the <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/payment-gateways/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">official marketplace</a>.</p>\n<h4>Ship locally and globally</h4>\n<p>Shipping with WooCommerce is highly configurable. Adjust the built-in settings to offer free shipping or flat rate shipping, limit your shipments to specific countries by setting up shipping zones, or open your store up to the world.</p>\n<p>Official extensions connect you with hundreds of local and international carriers  including <a href=\"https://woocommerce.com/products/royal-mail/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Royal Mail</a>, <a href=\"https://woocommerce.com/products/fedex-shipping-module/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">FedEx</a>, and <a href=\"https://woocommerce.com/products/australia-post-shipping-method/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Australia Post</a>  and let you integrate with <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/shipping-methods/inventory-fulfillment/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">inventory management and fulfilment providers</a>.</p>\n<p>There are also extensions available to add <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/shipping-methods/delivery-shipping-options/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">delivery and shipping options</a> and support strategies like buy-one-get-one free, free gifts, and add-ons. <a href=\"https://woocommerce.com/products/shipping/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooCommerce Shipping</a> supports real-time calculations and printing labels at home.</p>\n<h4>Design your store with themes and blocks</h4>\n<p>WooCommerce store design starts with a theme of your choice. There are hundreds of free and paid themes available, including <a href=\"https://woocommerce.com/storefront/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Storefront</a> by Automattic &#8212; it&#8217;s free to all stores and you can choose to have it installed for you during the guided setup.</p>\n<p>Storefront offers deep WooCommerce integration and prioritizes speed and uptime. You can add your brand and define your style by customizing Storefront yourself or adding one of several industry-themed <a href=\"https://woocommerce.com/product-category/themes/storefront-child-theme-themes/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Storefront child themes</a>.</p>\n<p>Personalize your store&#8217;s design even more with WooCommerce Blocks (available in WooCommerce 3.6 and above) &#8212; use them to add selections of or single products to any page, for a seamless blending of commerce into content.</p>\n<h4>Customize your store with extensions</h4>\n<p>The easiest way to add features and functionality to a WooCommerce store is with an extension:</p>\n<ul>\n<li>Sell anything &#8211; <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Subscriptions</a>, <a href=\"https://woocommerce.com/products/woocommerce-memberships/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Memberships</a>, <a href=\"https://woocommerce.com/products/woocommerce-bookings/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Bookings</a>, <a href=\"https://woocommerce.com/products/product-bundles/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Product Bundles</a>, and <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/product-type/\" rel=\"nofollow ugc\">more</a>. </li>\n<li>Make your store, your way &#8211; <a href=\"https://woocommerce.com/products/product-add-ons/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Product Add-Ons</a>, <a href=\"https://woocommerce.com/products/woocommerce-checkout-field-editor/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Checkout Field Editor</a>, <a href=\"https://woocommerce.com/products/woocommerce-additional-variation-images/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Additional Variation Images</a>, and <a href=\"https://woocommerce.com/customize-product-pages?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">more</a>.</li>\n<li>Customize your shipping options &#8211; <a href=\"https://woocommerce.com/products/table-rate-shipping/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Table Rate Shipping</a>, <a href=\"https://woocommerce.com/products/shipment-tracking/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Shipment Tracking</a>, live rates from <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/shipping-methods/shipping-carriers/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">top carriers</a>, and <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/shipping-methods/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">more</a>.</li>\n<li>Find your audience, market to them your way &#8211; <a href=\"https://woocommerce.com/products/google-product-feed/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Google Product Feed</a>, <a href=\"https://woocommerce.com/products/livechat/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">LiveChat</a>, <a href=\"https://woocommerce.com/products/amazon-ebay-integration/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Amazon/eBay marketplace integration</a>, and <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/marketing-extensions/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">more</a>.</li>\n<li>Drive sales  <a href=\"https://woocommerce.com/products/dynamic-pricing/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Dynamic Pricing</a>, <a href=\"https://woocommerce.com/products/smart-coupons/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Smart Coupons</a>, <a href=\"https://woocommerce.com/products/google-ads/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Google Ads</a>, and <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/marketing-extensions/promotions/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">more</a>.</li>\n</ul>\n<p>There are hundreds of official extensions reviewed by WooCommerce developers available on the WooCommerce.com marketplace, and many in the WordPress.org repository and on the wider web.</p>\n<h4>Control your data forever</h4>\n<p>WooCommerce gives you complete control  from taxes to stock levels to customer accounts. Add and remove extensions, change your stores design, and switch themes or hosts or payment service providers, all as you please.</p>\n<p>In a world increasingly aware of the importance of data protection, WooCommerce gives you full ownership over what is tracked and stored. If you opt to share <a href=\"https://woocommerce.com/usage-tracking/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">usage data</a> with us, your data is anonymized and kept secure. At any stage, you can opt out of all forms of tracking while still enjoying all of WooCommerce&#8217;s capabilities.</p>\n<p>With WooCommerce, your data belongs to you. One of the risks of using a hosted eCommerce platform the risk of losing your store if the provider closes. WooCommerce store data is future-proof &#8212; youre are free to export all your content and take your site  wherever you choose.</p>\n<h4>Why developers like WooCommerce</h4>\n<p>WooCommerce was originally created with developers in mind. Built with a REST API, it integrates with virtually any service. Store data can be accessed anywhere, anytime, 100% securely.</p>\n<p>WooCommerce allows developers to easily create, modify, and scale a store that meets clients specifications, and to make enhancements either with extensions or with customs solution.</p>\n<p>No matter the size of the store you want to build, WooCommerce has a robust framework that supports stores from basic to enterprise  with content and commerce in a single, central location.</p>\n<p>WooCommerce is audited by a dedicated team of developers who work across time zones to identify and patch any and all discovered bugs. There is comprehensive, easily-accessible documentation that is updated with each release. With our docs, youll learn how to create the site your client needs.</p>\n<h4>Join our growing community</h4>\n<p>WooCommerce is one of the fastest-growing eCommerce communities. Were proud that the helpfulness of the community and wealth of resources available online are frequently cited as reasons our users love it.</p>\n<p>There are 80+ <a href=\"https://woocommerce.com/woocommerce/meetups/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooCommerce Meetups</a> taking place in cities across the world that you can attend for free and even get involved in running. WooCommerce also has a regular presence at WordCamps across the globe  and wed love to meet you.</p>\n<p>If youre interested in contributing to WooCommerce weve got more than 350 contributors, and theres always room for more. Head to the <a href=\"https://github.com/woocommerce/woocommerce?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooCommerce GitHub Repository</a> to find out how you can pitch in.</p>\n<p>WooCommerce is currently 100% translated into 24 languages, including Danish, Ukrainian, and Persian. If youre interested in helping to localize WooCommerce by adding your local language, visit <a href=\"https://translate.wordpress.org/projects/wp-plugins/woocommerce?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">translate.wordpress.org</a>.</p>\n<h3>Contributors &amp; Developers</h3>\n<p>WooCommerce has been translated into 55 locales. Thank you to <a href=\"https://translate.wordpress.org/projects/wp-plugins/woocommerce/contributors\" rel=\"nofollow ugc\">the translators</a> for their contributions. <a href=\"https://translate.wordpress.org/projects/wp-plugins/woocommerce?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Translate WooCommerce into your language</a>.</p>\n<p>INTERESTED IN DEVELOPMENT?<br />\n<a href=\"https://plugins.trac.wordpress.org/browser/woocommerce/\" rel=\"nofollow ugc\">Browse the code</a>, check out the <a href=\"https://plugins.svn.wordpress.org/woocommerce/\" rel=\"nofollow ugc\">SVN repository</a>, or subscribe to the <a href=\"https://plugins.trac.wordpress.org/log/woocommerce/\" rel=\"nofollow ugc\">development log</a> by <a href=\"https://plugins.trac.wordpress.org/log/woocommerce/?limit=100&amp;mode=stop_on_copy&amp;format=rss\" rel=\"nofollow ugc\">RSS</a>.</p>\n\";s:12:\"installation\";s:2306:\"<h4>Minimum Requirements</h4>\n<ul>\n<li>PHP 7.2 or greater is recommended</li>\n<li>MySQL 5.6 or greater is recommended</li>\n</ul>\n<p>Visit the <a href=\"https://docs.woocommerce.com/document/server-requirements/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooCommerce server requirements documentation</a> for a detailed list of server requirements.</p>\n<h4>Automatic installation</h4>\n<p>Automatic installation is the easiest option &#8212; WordPress will handles the file transfer, and you wont need to leave your web browser. To do an automatic install of WooCommerce, log in to your WordPress dashboard, navigate to the Plugins menu, and click Add New.</p>\n<p>In the search field type WooCommerce, then click Search Plugins. Once youve found us,  you can view details about it such as the point release, rating, and description. Most importantly of course, you can install it by! Click Install Now, and WordPress will take it from there.</p>\n<h4>Manual installation</h4>\n<p>Manual installation method requires downloading the WooCommerce plugin and uploading it to your web server via your favorite FTP application. The WordPress codex contains <a href=\"https://wordpress.org/support/article/managing-plugins/#manual-plugin-installation\">instructions on how to do this here</a>.</p>\n<h4>Updating</h4>\n<p>Automatic updates should work smoothly, but we still recommend you back up your site.</p>\n<p>If you encounter issues with the shop/category pages after an update, flush the permalinks by going to WordPress &gt; Settings &gt; Permalinks and hitting Save. That should return things to normal.</p>\n<h4>Sample data</h4>\n<p>WooCommerce comes with some sample data you can use to see how products look; import sample_products.xml via the <a href=\"https://wordpress.org/plugins/wordpress-importer/\">WordPress importer</a>. You can also use the core <a href=\"https://docs.woocommerce.com/document/product-csv-importer-exporter/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">CSV importer</a> or our <a href=\"https://woocommerce.com/products/product-csv-import-suite/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">CSV Import Suite extension</a> to import sample_products.csv</p>\n\";s:3:\"faq\";s:2791:\"\n<h4>Where can I find WooCommerce documentation and user guides?</h4>\n<p>\n<p>For help setting up and configuring WooCommerce please refer to our <a href=\"https://docs.woocommerce.com/documentation/plugins/woocommerce/getting-started/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">user guide</a></p>\n<p>For extending or theming WooCommerce, see our <a href=\"https://docs.woocommerce.com/documentation/plugins/woocommerce/woocommerce-codex/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">codex</a>.</p>\n</p>\n<h4>Where can I get support or talk to other users?</h4>\n<p>\n<p>If you get stuck, you can ask for help in the <a href=\"https://wordpress.org/support/plugin/woocommerce\">WooCommerce Plugin Forum</a>.</p>\n<p>For help with paid extensions from WooCommerce.com, use <a href=\"https://woocommerce.com/my-account/tickets/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">our helpdesk</a>.</p>\n</p>\n<h4>Will WooCommerce work with my theme?</h4>\n<p>\n<p>Yes! WooCommerce will work with any theme, but may require some styling. Please see our <a href=\"https://docs.woocommerce.com/documentation/plugins/woocommerce/woocommerce-codex/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">codex</a> for help. If you&#8217;re looking for a theme with built in WooCommerce integration we recommend <a href=\"https://woocommerce.com/storefront/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">Storefront</a>.</p>\n</p>\n<h4>Where can I request new features, eCommerce themes, and extensions?</h4>\n<p>\n<p>You can vote on and request new features and extensions on our <a href=\"http://ideas.woocommerce.com/forums/133476-woocommerce?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooIdeas board</a></p>\n</p>\n<h4>Where can I report bugs or contribute to the project?</h4>\n<p>\n<p>Report bugs on the <a href=\"https://github.com/woocommerce/woocommerce/issues?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooCommerce GitHub repository</a>. You can also report them in our <a href=\"https://wordpress.org/support/plugin/woocommerce\">support forum</a>.</p>\n</p>\n<h4>Where can I find the REST API documentation?</h4>\n<p>\n<p>You can find the documentation of our REST API on the <a href=\"https://woocommerce.github.io/woocommerce-rest-api-docs/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooCommerce REST API Docs</a>.</p>\n</p>\n<h4>WooCommerce is awesome! Can I contribute?</h4>\n<p>\n<p>Yes you can! Join in on our <a href=\"https://github.com/woocommerce/woocommerce/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">GitHub repository</a>.</p>\n</p>\n\n\";s:9:\"changelog\";s:10620:\"<h4>4.0.1 &#8211; 2020-03-18</h4>\n<p><strong>WooCommerce</strong><br />\n* Enhancement &#8211; Update Action Scheduler to 3.1.4. #25966<br />\n* Enhancement &#8211; Bump Woocommerce Admin dependency to version 1.0.2. #25961<br />\n* Enhancement &#8211; Update dependency woocommerce/woocommerce-admin to v1.0.3 #25975<br />\n* Fix &#8211; Add <code>usage_count</code> meta before using it in a query. #25882<br />\n* Fix &#8211; Corrected argument type validation in plugin installer API. #25858<br />\n* Fix &#8211; Use standard <code>admin_url</code> function instead of absolute path. #25884<br />\n* Fix &#8211; Reverted the performance optimization made to variation saving. #25950<br />\n* Fix &#8211; Send dummy params to evaluate_cost method to detect validation errors. #25946<br />\n* Fix &#8211; Made the package shipping check more permissive. #25916<br />\n* Fix &#8211; Fix admin notes table does not exist errors when upgrading to 4.0.x. #25891</p>\n<p><strong>WooCommerce Admin</strong><br />\n* Enhancement &#8211; Onboarding: business step: add Google Ads extension install.<br />\n* Tweak &#8211; create database tables on an earlier hook to avoid conflicts with core WooCommerce.<br />\n* Fix &#8211; Add Report Extension Example: Add default props to ReportFilters.<br />\n* Fix &#8211; Product report sorting by SKU when some products don&#8217;t have SKUs.<br />\n* Fix &#8211; type warning on install timestamp in PHP 7.4.<br />\n* Fix &#8211; PHP error when WooCommerce core is Network Active on Multisites.<br />\n* Fix &#8211; missing database table errors on WooCommerce upgrade.<br />\n* Fix &#8211; undefined const <code>WC_ADMIN_VERSION_NUMBER</code> when WP &lt; 5.3<br />\n* Fix &#8211; Made the admin note loading more resilient to prevent failures when loading notes with invalid content_data. #3926<br />\n* Fix &#8211; Removed <code>replace_actionscheduler_store_class</code> function. #3936<br />\n* Fix &#8211; Rename Google Shopping image asset. #3931<br />\n* Fix &#8211; Fix calling protected <code>has_satisfied_dependencies</code> on outdated plugin. #3938<br />\n* Dev &#8211; Add Changelog script.<br />\n* Dev &#8211; Fix failing tests after WC core merge.<br />\n* Dev &#8211; Bump WooCommerce tested up to tag.<br />\n* Dev &#8211; Update prestart script so readme.txt stable tag is updated.</p>\n<p><strong>Action Scheduler</strong><br />\n* Fix &#8211; Re-create tables if needed on Scheduled Actions screen load. #492<br />\n* Fix &#8211; Add null action check on Scheduled Actions screen list rows. #493<br />\n* Fix &#8211; Fix context on ignored action log message. #481<br />\n* Fix &#8211; Restore scheduled action row and bulk action processing on WooCommerce Status screen. #487<br />\n* Fix &#8211; Include logs table re-create missed in #492. #495<br />\n* Fix &#8211; Ensure valid table name in save action. #498</p>\n<h4>4.0.0 &#8211; 2020-03-10</h4>\n<ul>\n<li>Enhancement &#8211; Included information about packages in the System Status Report. #25584</li>\n<li>Enhancement &#8211; New WooCommerce Admin. #25011</li>\n<li>Enhancement &#8211; Update dependency woocommerce/woocommerce-blocks to v2.5.12 #25587</li>\n<li>Enhancement &#8211; Updated Action Scheduler to 3.0.1. #25566</li>\n<li>Enhancement &#8211; Update dependency woocommerce/woocommerce-admin to v0.25.1. #25620</li>\n<li>Enhancement &#8211; Update dependency woocommerce/woocommerce-blocks to v2.5.13. #25696</li>\n<li>Enhancement &#8211; Added the WC Admin enabled db update notice. #25736</li>\n<li>Enhancement &#8211; Update Action Scheduler to version 3.1.1. #25745</li>\n<li>Enhancement &#8211; Update dependency woocommerce/woocommerce-blocks to v2.5.14. #25807</li>\n<li>Enhancement &#8211; Update dependency woocommerce/woocommerce-admin to v1.0. #25822</li>\n<li>Enhancement &#8211; Update dependency woocommerce/action-scheduler to v3.1.2. #25859</li>\n<li>Performance &#8211; Improved the client-side preparation for variation saving. #25382</li>\n<li>Tweak &#8211; Enhance order details and payment summary. #25504</li>\n<li>Tweak &#8211; Increase new onboarding group test to 50%. #25501</li>\n<li>Tweak &#8211; Increased range and precision for <code>min_price</code> and <code>max_price</code> in the product meta lookup table. #25253</li>\n<li>Tweak &#8211; Move action scheduler to external via composer. #25404</li>\n<li>Tweak &#8211; Only update the customer IP address when order gets created from admin. #25137</li>\n<li>Tweak &#8211; Remove WooCommerce Admin install alert. #25502</li>\n<li>Tweak &#8211; Removed WC Admin from the Setup Wizard if it&#8217;s already active. #25562</li>\n<li>Tweak &#8211; Set email header table width to 100% for full width. #25294</li>\n<li>Tweak &#8211; Simplified MaxMind integration title. #25522</li>\n<li>Tweak &#8211; Update &#8216;Country&#8217; to &#8216;Country / Region&#8217; label. #25530</li>\n<li>Tweak &#8211; WooCommerce.com plugins auto-install and update improvement. #25532</li>\n<li>Fix &#8211; Add missing closing tag. #25319</li>\n<li>Fix &#8211; Added validation to the cost field for flat rate shipping. #24919</li>\n<li>Fix &#8211; Address in shipping calculator malformed for Canada. #25149</li>\n<li>Fix &#8211; Adjusted package shipping rates to only be visible when a full address is entered. #25128</li>\n<li>Fix &#8211; Check for WooCommerce Admin feature plugin class before adding install notice. #25395</li>\n<li>Fix &#8211; Corrected the performance issues caused by the Cash-On-Delivery plugin&#8217;s initialization process. #25512</li>\n<li>Fix &#8211; Duplicate webhook deliveries. #25183</li>\n<li>Fix &#8211; Fix fatal error that would occur when calling the WC_Payment_Gateways::set_current_gateway() function and there is no WC session. #25322</li>\n<li>Fix &#8211; Fixed adding multiple items to cart from any product listing page. #24818</li>\n<li>Fix &#8211; Fixed get_discount function return type. #25567</li>\n<li>Fix &#8211; Fixed possible multiple is_vat_exempt order meta in order creation. #25426</li>\n<li>Fix &#8211; Fixed wrong context help for translators. #25496</li>\n<li>Fix &#8211; Make <code>WC_Shipping::is</code> package shippable less strict. #25386</li>\n<li>Fix &#8211; Prevent undefined <code>wp_delete_user()</code> error while removing inactive accounts. #25489</li>\n<li>Fix &#8211; Removed constants and autoloader from the uninstall script. #25589</li>\n<li>Fix &#8211; Removed the lowercase conversion for product search terms that caused incorrect results to case sensitive searches. #25314</li>\n<li>Fix &#8211; Restored the default behavior of &#8220;Shipping destination&#8221; option. #25571</li>\n<li>Fix &#8211; Set image CSS on emails to be <code>max-width: 100%</code> so that they don&#8217;t break the email template. #24764</li>\n<li>Fix &#8211; Setup wizard shipping setup verification logic correction. #25540</li>\n<li>Fix &#8211; Fixed typo in label. #25645</li>\n<li>Fix &#8211; Added the missing &#8220;bestRating&#8221; and &#8220;worstRating&#8221; params to structured schema data. #25667</li>\n<li>Fix &#8211; Replaced deprecated Jetpack::is_staging_site call. #25670</li>\n<li>Fix &#8211; Avoid <code>Automattic\\Jetpack\\Constants</code> in main woocommerce class. #25697</li>\n<li>Fix &#8211; Change the namespace of the WC Admin Package class and update WC Admin package. #25701</li>\n<li>Fix &#8211; Unmark completed AS migration during the update to make sure AS migration happens. #25707</li>\n<li>Fix &#8211; configure Jetpack plugin before trying to connect/register. #25742</li>\n<li>Fix &#8211; Check configure exists before calling to support older JP versions. #25747</li>\n<li>Fix &#8211; Prevent edited attribute notice being hidden by new dashboard. #25757</li>\n<li>Fix &#8211; Corrected the cache invalidation behavior of order item CRUD actions. #25734</li>\n<li>Fix &#8211; Don&#8217;t show duplicated update notifications on Woo Screens. #25828</li>\n<li>Fix &#8211; Escape MaxMind database URL. #25682</li>\n<li>Fix &#8211; System status report should correctly identify inactive package. #25830</li>\n<li>Dev &#8211; Added support for placeholder attribute in quantity inputs. #25418</li>\n<li>Dev &#8211; Added <code>tax_status</code> and <code>tax_class</code> columns to the product meta data lookup table. #25428</li>\n<li>Dev &#8211; Introduced <code>woocommerce_top_rated_widget_args</code> filter. #25320</li>\n<li>Dev &#8211; Introduced <code>woocommerce_admin_process_variation_object</code> hook. #24929</li>\n<li>Dev &#8211; Added actions before and after grouped product list to allow adding custom rows. #25093</li>\n<li>Dev &#8211; Added filter to tweak whether a product has enough stock while attempting to pay for an order. #25230</li>\n<li>Dev &#8211; Added the <code>automattic/jetpack-constants</code> package and replace PHP constant definition checks with it. #25516</li>\n<li>Dev &#8211; Added a <code>triggerHandler</code> called <code>checkout_place_order_success</code> on a successful order during the checkout process. #25289</li>\n<li>Dev &#8211; Allow filtering of default meta value inside <code>WC_Data::get_meta</code> even if meta key not found. #24066</li>\n<li>Dev &#8211; Includes Emogrifier composer package instead of including into <code>includes/libraries</code>. #25525</li>\n<li>Dev &#8211; Introduce <code>WC_Countries::get_vat_countries</code> for returning a list of countries that uses VAT and refactor <code>WC_Countries::get_european_union_countries</code> with backward compatibility and deprecation to remove the VAT functionality from there. Brexit, remove GB from <code>WC_Countries::get_european_union_countries</code>. #24943</li>\n<li>Dev &#8211; Introduced <code>woocommerce_download_product_filepath</code> filter. #25485</li>\n<li>Dev &#8211; Introduced <code>woocommerce_email_content_type</code> filter. #25405</li>\n<li>Dev &#8211; Updated <code>woocommerce_email_from_name</code> and <code>woocommerce_email_from_address</code> filter arguments to include <code>wp_email()</code> default data. #25405</li>\n<li>Dev &#8211; Introduced <code>woocommerce_shortcode_products_query_results</code> filter. #25573</li>\n<li>Dev &#8211; Removed <code>hash_equals()</code> polyfill as it was no longer needed. #25474</li>\n<li>Dev &#8211; Removed unused <code>.order-actions</code> CSS. #25581</li>\n<li>Dev &#8211; Applies woocommerce_maxmind_geolocation_database_path in MaxMind database migration. #25681</li>\n<li>Dev &#8211; Support both .data() and .dataset for formdata in add to cart requests. #25726</li>\n</ul>\n<p><a href=\"https://raw.githubusercontent.com/woocommerce/woocommerce/master/CHANGELOG.txt\" rel=\"nofollow ugc\">See changelog for all versions</a>.</p>\n\";s:11:\"screenshots\";s:1035:\"<ol><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-1.jpg?rev=2074177\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-1.jpg?rev=2074177\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-2.jpg?rev=2074177\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-2.jpg?rev=2074177\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-3.jpg?rev=2074177\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-3.jpg?rev=2074177\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-4.jpg?rev=2074177\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-4.jpg?rev=2074177\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-5.jpg?rev=2074177\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-5.jpg?rev=2074177\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-6.jpg?rev=2074177\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-6.jpg?rev=2074177\" alt=\"\"></a></li></ol>\";s:7:\"reviews\";s:14520:\"<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Remove sticky top bar</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"3 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"3\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/blackyangell\"><img alt=\'\' src=\'http://1.gravatar.com/avatar/d5838e74c44bfab30bb3416aa2ef78a2?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://1.gravatar.com/avatar/d5838e74c44bfab30bb3416aa2ef78a2?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/blackyangell\" class=\"reviewer-name\">blackyangell</a> on <span class=\"review-date\">March 26, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Remove sticky top bar please, it doesn\'t help but takes page space.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Site crashed after update</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/tave89\"><img alt=\'\' src=\'http://0.gravatar.com/avatar/6c89900b07dcfd1786731b8406e41e9a?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://0.gravatar.com/avatar/6c89900b07dcfd1786731b8406e41e9a?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/tave89\" class=\"reviewer-name\">tave89</a> on <span class=\"review-date\">March 26, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Why do you publish an update which causes so much harm?!?!\nWould be nice if you can provide the previous version.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">4.0 is a mistake, not usable</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/rarodev\"><img alt=\'\' src=\'http://0.gravatar.com/avatar/fda9b1168ccf993b741efe6105eba88e?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://0.gravatar.com/avatar/fda9b1168ccf993b741efe6105eba88e?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/rarodev\" class=\"reviewer-name\">rarodev</a> on <span class=\"review-date\">March 25, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Very low performance after update. I thought they add admin plugin to main plugin because fixing low performance of the first one.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Beware, you can&#039;t change the image size in the product page layout</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/blauwevogel\"><img alt=\'\' src=\'http://2.gravatar.com/avatar/5cef33fd5483af900e2114b5c11a1a3b?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://2.gravatar.com/avatar/5cef33fd5483af900e2114b5c11a1a3b?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/blauwevogel\" class=\"reviewer-name\">blauwevogel</a> on <span class=\"review-date\">March 23, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Please developers who set up webshops! \nDon\'t rely on Woocommerce because you will be disappointed with the way templates work. Because Woocommerce uses a custom page template, most themes will put that behind a paywall. \nLeaving you only allowed to edit the blog and page template, and not the product page.\nAlso they use a hierarchy so you have to edit the order of meta details. \nI have never seen such a poorly written plugin before.\n\nPLEASE STAY ALERT\nDON\'T DOWNLOAD IF YOU ARE NOT SURE WHAT YOU ARE DOING!!</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">i have fucked always with product image size .</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/sohan25\"><img alt=\'\' src=\'http://0.gravatar.com/avatar/088b3057150bbee0e4adda036f2d898d?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://0.gravatar.com/avatar/088b3057150bbee0e4adda036f2d898d?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/sohan25\" class=\"reviewer-name\">sohan25</a> on <span class=\"review-date\">March 21, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">let me know how i can fix the product image size . why the image getting small ? . dont you think it is poor function </div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">The latest update doesn&#039;t work correctly</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"2 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"2\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/flaviorm\"><img alt=\'\' src=\'http://1.gravatar.com/avatar/4cca301d91eba67341d1d89e16d8d5ec?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://1.gravatar.com/avatar/4cca301d91eba67341d1d89e16d8d5ec?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/flaviorm\" class=\"reviewer-name\">flaviorm</a> on <span class=\"review-date\">March 21, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">My website was broken with the last update. It only works correctly if I disable woocommerce. The latest update does not work.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">I love WooCommerce but...</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/hurri500\"><img alt=\'\' src=\'http://0.gravatar.com/avatar/c30d342e811e1955cd68ac1ab90124f1?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://0.gravatar.com/avatar/c30d342e811e1955cd68ac1ab90124f1?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/hurri500\" class=\"reviewer-name\">hurri500</a> on <span class=\"review-date\">March 21, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\"><strong>WooCommerce Admin</strong> is a pile of $#!&amp;!!! Why on earth would you foist something on us that no one wanted to begin with? At least let us have the option of disabling it.\nI suppose it may be time to consider another ecommerce add-on...</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Order details are not received with new version.</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/feaguilarvera\"><img alt=\'\' src=\'http://2.gravatar.com/avatar/2a6fb293ac3d5290cbaebaef1d4d7bf8?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://2.gravatar.com/avatar/2a6fb293ac3d5290cbaebaef1d4d7bf8?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/feaguilarvera\" class=\"reviewer-name\">feaguilarvera</a> on <span class=\"review-date\">March 20, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">I updated to the latest version 4.0.1, and the detail of the orders are empty. We cannot know what customers bought.\n\nI had to immediately go back to version 3.9.2\n\nThis is unfortunate.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">my website crash after this last update</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/jetset257\"><img alt=\'\' src=\'http://1.gravatar.com/avatar/ddf770df1584425a8b4a5d411342f6ea?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://1.gravatar.com/avatar/ddf770df1584425a8b4a5d411342f6ea?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/jetset257\" class=\"reviewer-name\">jetset257</a> on <span class=\"review-date\">March 20, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Hi, \n\nI updated the woocomerce with this latest version and my website currently under construction crashed directly. Luckily I made a backup after installing version 4.0. I use the woocomerce addons and the woocomerce jet crocoblock extension and everything was working perfectly until this last update . What can I do to continue with the new woocommerce update? </div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Members</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/icetechy\"><img alt=\'\' src=\'http://2.gravatar.com/avatar/ba5ab21f203d4511a0660ca04e811777?s=16&#038;d=monsterid&#038;r=g\' srcset=\'http://2.gravatar.com/avatar/ba5ab21f203d4511a0660ca04e811777?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' /></a><a href=\"https://profiles.wordpress.org/icetechy\" class=\"reviewer-name\">icetechy</a> on <span class=\"review-date\">March 19, 2020</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Hi I am using the members plugin along with Elementor. I created some sections via elementor and I want this custom sections to be viewed only by members who have subscribed, but its not working. only shortcodes work to protect content, but the only con is that it doesn\'t show the notification message that that particular content is for only members. </div>\n</div>\n\";}s:17:\"short_description\";s:120:\"WooCommerce is a flexible, open-source eCommerce solution built on WordPress. Sell anything, anywhere and make your way.\";s:13:\"download_link\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.4.0.1.zip\";s:11:\"screenshots\";a:6:{i:1;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-1.jpg?rev=2074177\";s:7:\"caption\";b:0;}i:2;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-2.jpg?rev=2074177\";s:7:\"caption\";b:0;}i:3;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-3.jpg?rev=2074177\";s:7:\"caption\";b:0;}i:4;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-4.jpg?rev=2074177\";s:7:\"caption\";b:0;}i:5;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-5.jpg?rev=2074177\";s:7:\"caption\";b:0;}i:6;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-6.jpg?rev=2074177\";s:7:\"caption\";b:0;}}s:8:\"versions\";a:85:{s:5:\"3.0.0\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.0.zip\";s:5:\"3.0.1\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.1.zip\";s:5:\"3.0.2\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.2.zip\";s:5:\"3.0.3\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.3.zip\";s:5:\"3.0.4\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.4.zip\";s:5:\"3.0.5\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.5.zip\";s:5:\"3.0.6\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.6.zip\";s:5:\"3.0.7\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.7.zip\";s:5:\"3.0.8\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.8.zip\";s:5:\"3.0.9\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.3.0.9.zip\";s:5:\"3.1.